class Vector3D {
    constructor(x = 0, y = 0, z = 0) {
        if (!Number.isFinite(x) || !Number.isFinite(y) || !Number.isFinite(z)) {
            throw new Error('Coordinates must be finite numbers');
        }
        Object.defineProperties(this, {
            'x': {
                value: x === 0 ? 0 : x,
                writable: false,
                enumerable: true,
                configurable: false
            },
            'y': {
                value: y === 0 ? 0 : y,
                writable: false,
                enumerable: true,
                configurable: false
            },
            'z': {
                value: z === 0 ? 0 : z,
                writable: false,
                enumerable: true,
                configurable: false
            }
        });
        Object.freeze(this);
    }
    // ... other methods ...

    project(onto) {
        if (!(onto instanceof Vector3D)) {
            throw new Error('Parameter must be a Vector3D instance');
        }
        const dotProduct = this.dot(onto);
        const ontoLengthSquared = onto.lengthSquared();
        if (Math.abs(ontoLengthSquared) < Number.EPSILON) {
            throw new Error('Cannot project onto zero vector');
        }
        const scalingFactor = dotProduct / ontoLengthSquared;
        return new Vector3D(onto.x * scalingFactor, onto.y * scalingFactor, onto.z * scalingFactor);
    }
}
