   }

   // Method to trim the curve/surface based on a given interval or a set of intervals
   trim(intervalOrIntervals) {
      throw new new Error("Method 'trim' must be implemented.");
   }

   // Method to perform a subdivision operation, creating a new NURBS object with more control points
   subdivide(level) {
      throw new Error("Method 'subdivide' must be implemented.");
