```javascript
class NurbsSurface {
    constructor(controlPointsU, controlPointsV, knotsU, knotsV, weights, degreeU, degreeV) {
        this.controlPointsU = controlPointsU;
        this.controlPointsV = controlPointsV;
        this.knotsU = knotsU;
        this.knotsV = knotsV;
        this.weights = weights;
        this.degreeU = degreeU;
        this.degreeV = degreeV;
    }

    getControlPointsV() {
        return this.controlPointsV;
    }
}
