class Intersection {
    constructor(curveA, curveB, surfaceIndexA = null, surfaceIndexB = null, uParam = null, vParam = null) {
        this.curveA = curveA;
        this.curveB = curveB;
        this.surfaceIndexA = surfaceIndexA;
        this.surfaceIndexB = surfaceIndexB;
        this.uParam = uParam;
        this.vParam = vParam;
    }
}
