class NurbsCurve3D {
    getControlPointWeight(index) {
        if (index < 0 || index >= this.controlPoints.length) {
            throw new Error('Index is out of bounds for control points array.');
        }
        return this.controlPoints[index][3]; // Assuming the last element is the weight
    }
}
