export class testing {
    runTests() {
        const testInstance = new tests();
        // iterate over each method in the tests class and run it.
        for (const method in testInstance) {
           if(typeof testInstance[method] === 'function'){
                console.log('---------------------------------------------------------------------------------');
                console.log('Running test: ' + method);
                try {
                    const result = testInstance[method]();
                    if (result) {
                        console.log('Test passed: ' + method);
                    } else {
                       console.log('Test failed: ' + method);
                    }
                } catch (e) {
                    console.log('Error in test: ' + method);
                    console.error(e);
                }
            }
        }
    }
}
