export class Utils {
    static basisFunction(i, degree, u, knots) {
        if (degree === 0) {
            if (u >= knots[i] && u < knots[i + 1]) {
                return 1;
            } else {
                return 0;
            }
        }

        const alpha1 = (knots[i + degree] !== knots[i]) 
            ? (u - knots[i]) / (knots[i + degree] - knots[i]) 
            : 0;
        const alpha2 = (knots[i + degree + 1] !== knots[i + 1]) 
            ? (knots[i + degree + 1] - u) / (knots[i + degree + 1] - knots[i + 1]) 
            : 0;

        return alpha1 * this.basisFunction(i, degree - 1, u, knots) + 
               alpha2 * this.basisFunction(i + 1, degree - 1, u, knots);
    }

    static deBoor(degree, controlPoints, knots, weights, u) {
        const n = controlPoints.length - 1;
        const span = this.findSpan(n, degree, u, knots);

        const basis = [];
        for (let i = 0; i <= degree; i++) {
            basis.push(this.basisFunction(span - degree + i, degree, u, knots));
        }

        let point = new Point3D(0, 0, 0);
        let weightSum = 0;

        for (let i = 0; i <= degree; i++) {
            const w = weights[span - degree + i] * basis[i];
            point = point.add(controlPoints[span - degree + i].scale(w));
            weightSum += w;
        }

        return point.scale(1 / weightSum);
    }

    static findSpan(n, degree, u, knots) {
        if (u >= knots[n + 1]) return n;
        if (u < knots[0]) return 0;

        let low = 0;
        let high = n + 1;
        let mid = Math.floor((low + high) / 2);

        while (u < knots[mid] || u >= knots[mid + 1]) {
            if (u < knots[mid]) {
                high = mid;
            } else {
                low = mid;
            }
            mid = Math.floor((low + high) / 2);
        }

        return mid;
    }
}
```

