        if (Math.abs(evaluatedPoint.getX() - 1) > 0.001) {
            testPassed = false;
            message += `evaluate.x failed: expected 1, got ${evaluatedPoint.getX()}; `;
        }
        if (Math.abs(evaluatedPoint.getY() - 1) > 0.001) {
