        testPassed = false;
        message += `evaluatePartialDerivative failed: expected vectors, got ${partialDerivativeU}, ${partialDerivativeV}; `;
    }

    // Test normal method
    const normalVector = surface.normal(0.5, 0.5);
    if (!normalVector) {
        testPassed = false;
        message += `normal failed: expected a vector, got ${normalVector}; `;
    } else {
        // Normal of a flat plane in Z direction should be parallel to Z-axis
        if (Math.abs(normalVector.getX()) > 0.001 || Math.abs(normalVector.getY()) > 0.001) {
            testPassed = false;
            message += `normal direction failed: expected near Z-axis, got ${normalVector.toArray()}; `;
