export class NurbsSurface {
    // ... existing methods ...

    evaluatePartialDerivative(u, v, orderU, orderV) {
        if (u < this.knotsU[0] || u > this.knotsU[this.knotsU.length - 1] || 
            v < this.knotsV[0] || v > this.knotsV[this.knotsV.length - 1]) {
            return null;
        }
        if (orderU < 0 || orderV < 0) {
            return null;
        }
        const n = this.controlPoints.length - 1;
        const m = this.controlPoints[0].length - 1;
        const p = this.degreeU;
        const q = this.degreeV;
        const U = this.knotsU;
        const V = this.knotsV;
        const P = this.controlPoints;
        const W = this.weights;

        let Su = [];
        for (let i = 0; i <= m; i++) {
            let controlPoints = [];
            let weights = [];
            for (let j = 0; j <= n; j++) {
                controlPoints.push(P[j][i]);
                weights.push(W[j][i]);
            }
            Su.push(this.deBoorDerivative(p, controlPoints, U, weights, u, orderU));
        }

        let controlPoints = [...Su];
        let weights = Array(Su.length).fill(1);
        const derivative = this.deBoorDerivative(q, controlPoints, V, weights, v, orderV);

        if (!derivative || isNaN(derivative.getX()) || isNaN(derivative.getY()) || isNaN(derivative.getZ())) {
            return null;
        }
        return derivative;
    }

    deBoorDerivative(degree, controlPoints, knots, weights, u, order) {
        if (order < 0) {
            return 0;
        }
        const n = controlPoints.length - 1;
        const span = this.findSpan(n, degree, u, knots);
        let ders = [];

        // Initialize derivative array
        for (let k = 0; k <= order; k++) {
            ders.push([]);
        }

        // Initialize with control points
        for (let i = 0; i <= degree; i++) {
            const controlPoint = controlPoints[span - degree + i] instanceof Point3D 
                ? controlPoints[span - degree + i] 
                : new Point3D(
                    controlPoints[span - degree + i].x ?? controlPoints[span - degree + i].getX(),
                    controlPoints[span - degree + i].y ?? controlPoints[span - degree + i].getY(),
                    controlPoints[span - degree + i].z ?? controlPoints[span - degree + i].getZ()
                );
            ders[0].push(controlPoint);
        }

        // Compute derivatives using de Boor's algorithm
        for (let k = 1; k <= order; k++) {
            for (let i = 0; i <= degree - k; i++) {
                let alpha = (u - knots[span - degree + i + k]) / (knots[span + 1 + i] - knots[span - degree + i + k]);
                if (isNaN(alpha)) alpha = 0;

                let point = ders[k - 1][i + 1].sub(ders[k - 1][i]).scale(degree - k + 1);
                ders[k].push(point.scale(1 / (knots[span + 1 + i] - knots[span - degree + i + k])));
            }
        }

        // Compute the derivative vector
        let vector = new Vector3D(0, 0, 0);
        for (let i = 0; i <= degree; i++) {
            const basisValue = this.basisFunctionDerivative(span - degree + i, degree, u, knots, order);

            // Convert Point3D to Vector3D if needed
            const pointAsVector = ders[0][i] instanceof Vector3D 
                ? ders[0][i] 
                : new Vector3D(ders[0][i].x, ders[0][i].y, ders[0][i].z);

            vector = vector.add(pointAsVector.scale(basisValue * weights[span - degree + i]));
        }

        // Compute weight sum for the derivative
        let weightSum = 0;
        for (let i = 0; i <= degree; i++) {
            weightSum += this.basisFunctionDerivative(span - degree + i, degree, u, knots, order) * weights[span - degree + i];
        }

        // Return normalized derivative
        if (weightSum === 0) {
            return new Vector3D(0, 0, 0);
        }
        return vector.scale(1 / weightSum);
    }

    basisFunctionDerivative(i, degree, u, knots, order) {
        if (order < 0) {
            return 0;
        }
        if (order === 0) {
            return this.basisFunction(i, degree, u, knots);
        }
        if (degree === 0) {
            return 0;
        }

        let firstPart = 0;
        let secondPart = 0;

        if (knots[i + degree] !== knots[i]) {
            firstPart = degree / (knots[i + degree] - knots[i]) * 
                this.basisFunctionDerivative(i, degree - 1, u, knots, order - 1);
        }

        if (knots[i + degree + 1] !== knots[i + 1]) {
            secondPart = -degree / (knots[i + degree + 1] - knots[i + 1]) * 
                this.basisFunctionDerivative(i + 1, degree - 1, u, knots, order - 1);
        }

        return firstPart + secondPart;
    }

    // ... rest of the existing methods ...
