export class tests {
    GeometricIntersectionTest() {
        const result = new TestResult('GeometricIntersectionTest');

        const lineOrigin = new Point3D(0, 0, 0);
        const lineDirection = new Vector3D(0, 0, 1);
        const planeOrigin = new Point3D(0, 0, 5);
        const planeNormal = new Vector3D(0, 0, 1);

        const linePlaneIntersection = (origin, direction, planeOrigin, planeNormal) => {
            const epsilon = 1e-10;
            const denominator = planeNormal.dot(direction);

            if (Math.abs(denominator) < epsilon) {
                return null;
            }

            const v = planeOrigin.sub(origin);
            const t = planeNormal.dot(v) / denominator;

            return origin.add(direction.scale(t));
        };

        const intersection = linePlaneIntersection(
            lineOrigin, 
            lineDirection, 
            planeOrigin, 
            planeNormal
        );

        result.addCheck(
            intersection !== null, 
            'Line-plane intersection calculated', 
            'Line-plane intersection failed'
        );

        if (intersection) {
            result.addCheck(
                Math.abs(intersection.getX()) < 0.001 && 
                Math.abs(intersection.getY()) < 0.001 && 
                Math.abs(intersection.getZ() - 5) < 0.001,
                'Intersection point at expected location', 
                'Incorrect intersection point location'
            );
        }

        const p1 = new Point3D(0, 0, 0);
        const p2 = new Point3D(10, 10, 0);
        const p3 = new Point3D(0, 10, 0);
        const p4 = new Point3D(10, 0, 0);

        const lineSegmentIntersection = (a, b, c, d) => {
            const x1 = a.getX(), y1 = a.getY();
            const x2 = b.getX(), y2 = b.getY();
            const x3 = c.getX(), y3 = c.getY();
            const x4 = d.getX(), y4 = d.getY();

            const den = (y4 - y3) * (x2 - x1) - (x4 - x3) * (y2 - y1);

            if (Math.abs(den) < 1e-10) {
                return null;
            }

            const ua = ((x4 - x3) * (y1 - y3) - (y4 - y3) * (x1 - x3)) / den;
            const ub = ((x2 - x1) * (y1 - y3) - (y2 - y1) * (x1 - x3)) / den;

            if (ua >= 0 && ua <= 1 && ub >= 0 && ub <= 1) {
                const x = x1 + ua * (x2 - x1);
                const y = y1 + ua * (y2 - y1);
                return new Point3D(x, y, 0);
            }

            return null;
        };

        const segmentIntersection = lineSegmentIntersection(p1, p2, p3, p4);

        result.addCheck(
            segmentIntersection !== null, 
            'Line segment intersection calculated', 
            'Line segment intersection failed'
        );

        if (segmentIntersection) {
            result.addCheck(
                Math.abs(segmentIntersection.getX() - 5) < 0.001 && 
                Math.abs(segmentIntersection.getY() - 5) < 0.001,
                'Intersection point at expected location', 
                'Incorrect intersection point location'
            );
        }

        return result.getSummary();
    }
}
